<script>
$(document).ready(function(){
  console.log("open");
});
function toast(a,b) {
const Toast = Swal.mixin({
  toast: true,
  position: 'top-end',
  showConfirmButton: false,
  timer: 3000
})

Toast.fire({
  type: b,
  title: a
})
}
$( "#what" ).autocomplete({
  source: function (request,response) {
      $.ajax({
          url: "config/searchdata.php",
          data: {
              word:request.term
          },
          dataType: "json",
            method : "POST", 
          success: function (data) {
              console.log("jos");
              var respon=$.map(data,function(obj){
                  console.log(obj);
                  return obj;
              });
              response(respon);
          }
      });
  }
});


var id="";
function calculate(){
    var api="";
    console.log(id);
    var id_negara = $('#what').val();

    if (id_negara=="") {
      toast("Please select the country","error");
      $("#mycontent").slideUp();
    } else {
      $("#mycontent").slideDown();
      toast("Price has been Calculate","success");
    }
    api="config/get_data.php"
    setTimeout(function(){
      $.ajax({
        type: 'POST',
        url: api,
        data:
        {
          id_negara : id_negara,
        },
        success:function(resource)
        {
          $('#get_data').html(resource);
        },
        complete: function(){
                $('span#label_loading').hide();
        }
      })

    }, 500)
}
</script>